import React, { useState } from 'react'
import './GameItem.css'
import { useNavigate } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { deleteItemFromCart, setItemInCart } from '../../store/cartSlise/getCartSlise'
import { setCurrentGame } from '../../store/gameSlise/getGameSlise'

const GameItem = ({item, id}) => {
    const navigate = useNavigate()
    const dispatch = useDispatch()
    const cart = useSelector(state => state.cart.cartState)
    const haveIdItem = cart.some((some) => some.id == item.id)
    
    const changeItemInCart = () =>{
        if(haveIdItem){
            dispatch(deleteItemFromCart(id))
        }
        else{
            dispatch(setItemInCart(item))
        }
    }
    
   
    return (<>
            <div className="game-item">   
                <img className="game-item__img" src={item.image} alt=""/>
                <div className="game-item__content">
                    <h2 className="game-item__title">{item.title}</h2><br/>
                    {
                        item.genres.map((genres, id) =>
                            (
                                <div key={`${id}_${genres}`} className="genres-item">
                                    <span className="genres-items">{genres}</span>
                                </div>
                            )
                            )
                    }
                </div>
                <span className="game-item__price">{item.price} руб</span>
                <button onClick={changeItemInCart} className={`game-item__add-cart ${haveIdItem? 'grey' : 'purple'}`}>В корзину</button>
            </div>
</>)
}

export default GameItem
