import React, { useState }  from 'react'
import { useNavigate } from 'react-router-dom';
import CartOpenItem from '../cart-open-item/CartOpenItem';
import './CartOpen.css'
import { useSelector } from 'react-redux'

function CartOpen() {
    const navigate = useNavigate();
    const cart = useSelector(state => state.cart.cartState)
    const [cartItem, setCartItem] = useState(true)
    const handleClick = () =>{
        navigate('/place');
      }

    return (
        <div className="cart-open">
        <div className="cart-open__wrap">
            {
                cart.lenght > 0 
                ?
                cart.map((item, id) =>(
                    <CartOpenItem id={item.id} title={item.title} price={item.price} key={`${item.title}_${id}`}/>
                )
                )
            
                
            :
            <h4 className="cart-open__empty">
                    корзина пуста
            </h4>
        }
        {
           cart.lenght > 0 
           ? 
           <div className="cart-open__item-two">
                    <div className="cart-open__total-price">
                        <span>
                            итого:
                        </span>
                        <span>
                            6120 руб
                        </span>
                    </div>
                    <button onClick={handleClick} className="cart-open__place-order">
                        оформить заказ
                    </button>
            </div>
            :
            null
        }
        </div>
    </div>
    )
}

export default CartOpen
