import { createSlice } from '@reduxjs/toolkit'

export const openSlice = createSlice({
    name: 'open',
    initialState: {
        openState: false,
    },
    reducers: {
        setTrueOpen: (state, action) => {
            state.openState = action.payload
          },
        setFalseOpen: (state, action) => {
            state.openState = action.payload
          },
    },
  })

export const { setCurrentOpen } = openSlice.actions

export default openSlice.reducer
