import React, {useState, useRef, useEffect} from 'react'
import CartOpen from '../cart-open/CartOpen';
import './Cart.css'
import { useSelector, useDispatch } from 'react-redux'


const Cart = () => {
    const dispatch = useDispatch()
    const cart = useSelector(state => state.cart.cartState)
    const [cartToggle, setCartToggle] = useState(false)
    const toggleSwitch = () =>{
        setCartToggle(!cartToggle)
    } 
    const refToogle = useRef()

    const handleOutsideClick = (e) => {
        if(!e.path.includes(refToogle.current)) {
            setCartToggle(false);
        }
        if(dispatch(setItemInCart)){
            setCartToggle(true);
        }
    }
    useEffect(() => {
        document.body.addEventListener('click', handleOutsideClick)
        
    }, [])

    return (
        <div ref={refToogle} className="cart">
            <div style={{cursor:'pointer'}} onClick={toggleSwitch}>
                cart
            </div>
            {
        cartToggle &&
            <CartOpen cart={cart}/>
            }
        </div>
    )
}

export default Cart;
