import React, {useState, useRef, useEffect} from 'react'
import CartOpen from '../cart-open/CartOpen';
import './Cart.css'
import { useSelector, useDispatch } from 'react-redux'
import { setTrueOpen } from '../../store/openSlise/getOpenSlise';

const Cart = () => {
    const dispatch = useDispatch()
    const cart = useSelector(state => state.cart.cartState)
    const open = useSelector(state => state.open.openState)
    const [cartToggle, setCartToggle] = useState(false)
    dispatch(setTrueOpen(cartToggle))
    const refToogle = useRef()
    const toggleSwitch = () =>{
        setCartToggle(!cartToggle)
    } 
    const handleOutsideClick = (e) => {
        if(!e.path.includes(refToogle.current)) {
            setCartToggle(false);
        }
    }
    useEffect(() => {
        document.body.addEventListener('click', handleOutsideClick);
    }, [])
    
    return (
        <div ref={refToogle} className="cart">
            <div className="cart-icon" style={{cursor:'pointer'}} onClick={toggleSwitch}>
                cart
                <span className={cart.length > 0 ? "cart-total-price" : ''}>{cart.length > 0 ? cart.length : ''}</span>
            </div>
            {
        cartToggle &&
            <CartOpen cart={cart}/>
            }
        </div>
    )
}

export default Cart;
