import React from 'react'
import { useDispatch } from 'react-redux'
import { setItemInCart } from '../../store/cartSlise/getCartSlise'

const OrderItem = ({id, item}) =>{
    const dispatch = useDispatch()

    const removeCartOpenItem = () =>{
        dispatch(deleteItemFromCart(id))
    }
    return (
        <div>
            <h3 className="cart-open__title">
                        {item.title}
                    </h3>
                    <span className="cart-open__price">{item.price} руб</span>
                    <button onClick={removeCartOpenItem} className="cart-open__deleted">x</button>
        </div>
    )
}

export default OrderItem
