import React, { useState } from 'react'
import './GameItem.css'
import { Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { deleteItemFromCart, setItemInCart } from '../../store/cartSlise/getCartSlise'

const GameItem = ({item, id}) => {
    const [toggleSwitch, setToggleSwitch] = useState(false)
    const dispatch = useDispatch()
    const addItemInCart = () =>{
            dispatch(setItemInCart(item))
            setToggleSwitch(true)
        }
    const removeItemInCart = () =>{
            dispatch(deleteItemFromCart(id))
            setToggleSwitch(false)
        }
   
    return (<>
            <Link className="game-item__link"  to="/">
            <div className="game-item">   
                <img className="game-item__img" src={item.image} alt=""/>
                <div className="game-item__content">
                    <h2 className="game-item__title">{item.title}</h2><br/>
                    {
                        item.genres.map((genres, id) =>
                            (
                                <div key={`${id}_${genres}`} className="genres-item">
                                    <span className="genres-items">{genres}</span>
                                </div>
                            )
                            )
                    }
                </div>
                <span className="game-item__price">{item.price} руб</span>
                <button onClick={toggleSwitch == false ? addItemInCart : removeItemInCart} className={`game-item__add-cart ${toggleSwitch == true? 'grey' : 'purple'}`}>В корзину</button>
            </div>
            </Link>
</>)
}

export default GameItem
