import React from 'react'
import './GamePage.css'
import { useDispatch, useSelector } from 'react-redux'
import { deleteItemFromCart, setItemInCart } from '../../store/cartSlise/getCartSlise'

const GamePage = () => {
    const dispatch = useDispatch()
    const item = useSelector(state => state.game.gameState)
    const cart = useSelector(state => state.cart.cartState)
    const haveIdItem = cart.some((some) => some.id == item.id)
    const changeItemInCart = () =>{
        if(haveIdItem){
            dispatch(deleteItemFromCart(cart.id))
        }
        else{
            dispatch(setItemInCart(cart))
        }
    }
    return (
        <div className="game-page">
            <h1 className="game-page__title">{ item.title }</h1>
                <div className="game-page__content">
                    <div className="game-page__left">
                        <iframe
                            width="90%"
                            height="400px"
                            src={item.video}
                            title="YouTube video player"
                            frameBorder="0"
                            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                        ></iframe>
                    </div>
                    <div className="game-page__right">
                    <img className="game-item__img" src={item.image} alt="" />
                            <p>{item.description}</p>
                            <p>метки для этого продукта:</p>
                            {
                        item.genres.map((genre, id) =>
                            (
                                <div key={`${id}_${genre}`} className="genres-item">
                                    <span className="genres-items">{genre}</span>
                                </div>
                            )
                            )
                    }
                        <div className="game-page__buy-game">
                        <span className="game-item__price">{item.price} руб</span>
                <button onClick={changeItemInCart} className={`game-item__add-cart ${haveIdItem? 'grey' : 'purple'}`}>В корзину</button>
                        </div>
                    </div>
                 </div>
    </div>
    )
}

export default GamePage
