import React, {useState, useRef, useEffect} from 'react'
import CartOpen from '../cart-open/CartOpen';
import './Cart.css'
import { useSelector } from 'react-redux'


const Cart = () => {
    const cart = useSelector(state => state.cart.cartState)
    const [cartToggle, setCartToggle] = useState(false)
    const toggleSwitch = () =>{
        setCartToggle(!cartToggle)
    } 
    const ref = useRef()

    const handleOutsideClick = (e) => {
        if(!e.path.includes(ref.current)) {
            setCartToggle(false);
        }
    }
    useEffect(() => {
        document.body.addEventListener(console.log('click',handleOutsideClick));
  
    }, [])

    return (
        <div className="cart">
            <div style={{cursor:'pointer'}} onClick={toggleSwitch}>
                cart
            </div>
            {
        cartToggle &&
            <CartOpen ref={ref} cart={cart}/>
            }
        </div>
    )
}

export default Cart;
